import React, { useState, useEffect } from 'react';

const RestfulApi = () => {
    const [country, setCountry] = useState('');
    const [cities, setCities] = useState([]);
    const apiKey = 'fed230a6e88686';

    useEffect(() => {
        fetchCountry();
    }, []);

    const fetchCountry = async () => {
        try {
            const response = await fetch(`https://ipinfo.io/json?token=${apiKey}`);
            const data = await response.json();

            // Extract the country name from the API response and set it in the state
            const countryName = data.country || '';
            setCountry(countryName);

            // Fetch cities using the country name
            if (countryName) {
                const citiesResponse = await fetch(`https://countries-cities.p.rapidapi.com/location/country/${countryName}/city/list?page=2&per_page=20&population=1501`, {
                    headers: {
                        'X-RapidAPI-Key': 'cc0abd92femshf147bdb212c0658p109b7ejsn6d1a58066eb4',
                        'X-RapidAPI-Host': 'countries-cities.p.rapidapi.com'
                    }
                });

                // Check if the response is successful
                if (citiesResponse.ok) {
                    const citiesData = await citiesResponse.json();
                    setCities(citiesData);
                } else {
                    console.error('Failed to fetch cities:', citiesResponse.statusText);
                }
            }
        } catch (error) {
            console.error('Error fetching data:', error);
        }
    };

    return (
        <div>
            <h2>Country: {country}</h2>
            <select name="city" id="city">
                <option value=""></option>
                {cities.map(city => (
                    <option key={city.id} value={city.name}>{city.name}</option>
                ))}
            </select>
        </div>
    );
};

export default RestfulApi;
