import React, { useState, useEffect } from 'react';
import Select from 'react-flags-select';
import './chatForm.css';
// import 'react-flags-select/css/react-flags-select.css';
import axios from 'axios';
// import { countries } from 'react-phone-number-input/input';

const ChatForm = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    gender: '',
    country: '',
    city: '',
    phoneNumber: '',
  });

  const [countriesData, setCountriesData] = useState([]);

  useEffect(() => {
    // setCountriesData(countries);
  }, []);

  const handleInputChange = (event) => {
    const { id, value } = event.target;
    setFormData((prevFormData) => ({
      ...prevFormData,
      [id]: value,
    }));
  };

  const handleCountrySelect = (countryCode) => {
    setFormData((prevFormData) => ({
      ...prevFormData,
      country: countryCode,
      city: '',
    }));
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    // Form validation logic
    if (formData.name.trim() === '') {
      console.log('Please enter your name.');
      return;
    }

    if (formData.email.trim() === '') {
      console.log('Please enter your email.');
      return;
    }

    if (formData.gender === '') {
      console.log('Please select your gender.');
      return;
    }

    if (formData.country === '') {
      console.log('Please select your country.');
      return;
    }

    if (formData.city === '') {
      console.log('Please select your city.');
      return;
    }

    if (formData.phoneNumber.trim() === '') {
      console.log('Please enter your phone number.');
      return;
    }

    // Form submission to server
    console.log('Form submitted:', formData);
    // Add your server submission logic here

    // Reset the form
    setFormData({
      name: '',
      email: '',
      gender: '',
      country: '',
      city: '',
      phoneNumber: '',
    });
  };

  return (
    <div className="chat-form-container">
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="name">Your Name</label>
          <input
            type="text"
            id="name"
            value={formData.name}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label htmlFor="email">Your E-Mail</label>
          <input
            type="email"
            id="email"
            value={formData.email}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label htmlFor="gender">Gender</label>
          <select
            id="gender"
            value={formData.gender}
            onChange={handleInputChange}
          >
            <option value="">Select one</option>
            <option value="male">Male</option>
            <option value="female">Female</option>
          </select>
        </div>
        <div>
          <label htmlFor="country">Country</label>
          <Select
            id="country"
            searchable
            options={countriesData.map((country) => ({
              label: country.label,
              value: country.value,
            }))}
            selected={formData.country}
            onSelect={handleCountrySelect}
          />
        </div>
        <div>
          <label htmlFor="city">City</label>
          <select
            id="city"
            value={formData.city}
            onChange={handleInputChange}
            disabled={!formData.country}
          >
            <option value="">Select city</option>
            {/* Render city options based on the selected country */}
          </select>
        </div>
        <div>
          <label htmlFor="phoneNumber">Phone Number</label>
          <input
            type="tel"
            id="phoneNumber"
            value={formData.phoneNumber}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <button type="submit">Submit</button>
        </div>
      </form>
    </div>
  );
};

export default ChatForm;
