import React, { useState } from 'react';
import { getCountries, getStatesByCountryCode } from 'country-state-city';

const RestfulApi2 = () => {
  const [selectedCountry, setSelectedCountry] = useState('');
  const [selectedState, setSelectedState] = useState('');
  const [selectedCity, setSelectedCity] = useState('');

  const countries = getCountries();
  const states = selectedCountry ? getStatesByCountryCode(selectedCountry) : [];
  const cities = selectedState
    ? states.find((state) => state.isoCode === selectedState)?.cities || []
    : [];

  const handleCountryChange = (event) => {
    setSelectedCountry(event.target.value);
    setSelectedState('');
    setSelectedCity('');
  };

  const handleStateChange = (event) => {
    setSelectedState(event.target.value);
    setSelectedCity('');
  };

  const handleCityChange = (event) => {
    setSelectedCity(event.target.value);
  };

  return (
    <div>
      <div>
        <label htmlFor="country">Country</label>
        <select name="country" id="country" value={selectedCountry} onChange={handleCountryChange}>
          <option value=""></option>
          {countries.map((country) => (
            <option key={country.isoCode} value={country.isoCode}>
              {country.name}
            </option>
          ))}
        </select>
      </div>
      <div>
        <label htmlFor="state">State</label>
        <select name="state" id="state" value={selectedState} onChange={handleStateChange}>
          <option value=""></option>
          {states.map((state) => (
            <option key={state.isoCode} value={state.isoCode}>
              {state.name}
            </option>
          ))}
        </select>
      </div>
      <div>
        <label htmlFor="city">City</label>
        <select name="city" id="city" value={selectedCity} onChange={handleCityChange}>
          <option value=""></option>
          {cities.map((city) => (
            <option key={city.name} value={city.name}>
              {city.name}
            </option>
          ))}
        </select>
      </div>
    </div>
  );
};

export default RestfulApi2;
