import React, { useState, useEffect } from 'react';

const RestfulApi = () => {
  const [cities, setCities] = useState([]);
  const apiKey = 'cc0abd92femshf147bdb212c0658p109b7ejsn6d1a58066eb4';

  useEffect(() => {
    fetchCities();
  }, []);

  const fetchCities = async () => {
    try {
      const response = await fetch(
        'https://countries-cities.p.rapidapi.com/location/country/all/city/list',
        {
          method: 'GET',
          headers: {
            'X-RapidAPI-Key': apiKey,
            'X-RapidAPI-Host': 'countries-cities.p.rapidapi.com'
          }
        }
      );
      const data = await response.json();

      // Extract the city names from the API response and set them in the state
      const cityNames = data.cities || [];
      setCities(cityNames);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  return (
    <div>
      <select name="city" id="city">
        <option value=""></option>
        {cities.map((city, index) => (
          <option key={index} value={city}>
            {city}
          </option>
        ))}
      </select>
    </div>
  );
};

export default RestfulApi;
