import { useRef, useState } from "react";
import styled from "styled-components";


const Validate = styled.p`
  /* color: red; */
`

const SimpleInput = (props) => {
  const nameInputRef = useRef();
  const [enteredName, setEnteredName] = useState("");
  const [enteredNameIsValid, setEnteredNameIsValid] = useState(false);
  const [enteredNameTouched, setEnteredNameTouched] = useState(false);

  const nameInputChangeHandler = (e) => {
      setEnteredName(e.target.value);
    console.log(enteredName);
  };



  const formSubmissionHandler = (e) => {
    e.preventDefault();

    setEnteredNameTouched(true)
    if (enteredName.trim().length < 6) {
      setEnteredNameIsValid(false)
      return
    }
    console.log(enteredName);
    setEnteredNameIsValid(true)
    setEnteredName("");

    const enteredValue = nameInputRef.current.value;
    // nameInputRef.current.value = ""  NOT IDEAL, DON'T MANIPULATE THE DOM.
    console.log("USE REF::", enteredValue);
  };

  const nameInputIsInvalid = !enteredNameIsValid && enteredNameTouched 

  const nameInputClasses = nameIn ? "form-control invalid" : "form-control"
  return (
    <form onSubmit={formSubmissionHandler}>
      <div className={nameInputClasses}>
        <label htmlFor="name">Your Name</label>
        { enteredNameIsValid && <Validate className="error-text">This is required</Validate>}
        <input
          className={enteredName.length < 6 ? "validates" : "not-validates"}
          type="text"
          value={enteredName}
          ref={nameInputRef}
          onChange={nameInputChangeHandler}
          id="name"
        />
      </div>
      <div className="form-actions">
        <button
          className={enteredName.length < 6 ? "valids" : "invalids"}
          type="submit"
        >
          {enteredName ? "Submit" : "Type"}
        </button>
      </div>
    </form>
  );
};

export default SimpleInput;
