import React, { useState, useEffect } from 'react';
import Select from 'react-flags-select';
import './chatForm.css';
import axios from 'axios';

const ChatForm = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    gender: '',
    country: '',
    city: '',
    phoneNumber: '',
  });

  const [countries, setCountries] = useState([]);
  const [cities, setCities] = useState([]);

  useEffect(() => {
    // Fetch country data from the "restcountries" API
    axios
      .get('https://restcountries.com/v3.1/all')
      .then((response) => {
        const countryData = response.data.map((country) => ({
          label: country.name.common,
          value: country.name.common,
        }));
        setCountries(countryData);
      })
      .catch((error) => console.log(error));
  }, []);

  useEffect(() => {
    // Fetch city data based on the selected country
    if (formData.country !== '') {
      axios
        .get(`https://city-api.example.com/cities?country=${formData.country}`)
        .then((response) => {
          const cityData = response.data.map((city) => ({
            label: city.name,
            value: city.name,
          }));
          setCities(cityData);
        })
        .catch((error) => console.log(error));
    }
  }, [formData.country]);

  const handleInputChange = (event) => {
    const { id, value } = event.target;
    setFormData((prevFormData) => ({
      ...prevFormData,
      [id]: value,
    }));
  };

  const handleCountrySelect = (countryCode) => {
    setFormData((prevFormData) => ({
      ...prevFormData,
      country: countryCode,
      city: '',
    }));
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    // Form validation
    const { name, email, gender, country, city, phoneNumber } = formData;

    if (!name.trim()) {
      console.log('Please enter your name.');
      return;
    }

    if (!email.trim()) {
      console.log('Please enter your email.');
      return;
    }

    if (!gender) {
      console.log('Please select your gender.');
      return;
    }

    if (!country) {
      console.log('Please select your country.');
      return;
    }

    if (!city) {
      console.log('Please select your city.');
      return;
    }

    if (!phoneNumber.trim()) {
      console.log('Please enter your phone number.');
      return;
    }

    // Form submission to server
    axios
      .post('https://your-server-endpoint', formData)
      .then((response) => {
        console.log('Form submitted successfully:', response.data);
        // Add any additional logic or feedback here
      })
      .catch((error) => {
        console.log('Form submission failed:', error);
        // Handle the error or display an error message
      });

    // Reset the form
    setFormData({
      name: '',
      email: '',
      gender: '',
      country: '',
      city: '',
      phoneNumber: '',
    });
  };

  return (
    <div className="chat-form-container">
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="name">Your Name</label>
          <input
            type="text"
            id="name"
            value={formData.name}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label htmlFor="email">Your E-Mail</label>
          <input
            type="email"
            id="email"
            value={formData.email}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label htmlFor="gender">Gender</label>
          <select
            id="gender"
            value={formData.gender}
            onChange={handleInputChange}
          >
            <option value="">Select one</option>
            <option value="male">Male</option>
            <option value="female">Female</option>
          </select>
        </div>
        <div>
          <label htmlFor="country">Country</label>
          <Select
            id="country"
            searchable
            options={countries}
            selected={formData.country}
            onSelect={handleCountrySelect}
          />
        </div>
        <div>
          <label htmlFor="city">City</label>
          <Select
            id="city"
            searchable
            options={cities}
            selected={formData.city}
            onSelect={(value) =>
              setFormData((prevFormData) => ({
                ...prevFormData,
                city: value,
              }))
            }
            disabled={!formData.country}
          />
        </div>
        <div>
          <label htmlFor="phoneNumber">Phone Number</label>
          <input
            type="tel"
            id="phoneNumber"
            value={formData.phoneNumber}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <button type="submit">Submit</button>
        </div>
      </form>
    </div>
  );
};

export default ChatForm;
