import React, { useState, useEffect } from 'react';
import './chatForm.css';

const ChatForm = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    gender: '',
    country: '',
  });

  const [countries, setCountries] = useState([]);

  useEffect(() => {
    // Fetch country data from the "restcountries" API
    fetch('https://restcountries.com/v3.1/all')
      .then(response => response.json())
      .then(data => setCountries(data))
      .catch(error => console.log(error));
  }, []);

  const handleInputChange = (event) => {
    const { id, value } = event.target;
    setFormData((prevFormData) => ({
      ...prevFormData,
      [id]: value,
    }));
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    // Form validation logic
    if (formData.name.trim() === '') {
      console.log('Please enter your name.');
      return;
    }

    if (formData.email.trim() === '') {
      console.log('Please enter your email.');
      return;
    }

    if (formData.gender === '') {
      console.log('Please select your gender.');
      return;
    }

    if (formData.country === '') {
      console.log('Please select your country.');
      return;
    }

    // Form submission to server
    console.log('Form submitted:', formData);
    // Add your server submission logic here

    // Reset the form
    setFormData({
      name: '',
      email: '',
      gender: '',
      country: '',
    });
  };

  return (
    <div className="chat-form-container">
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="name">Your Name</label>
          <input
            type="text"
            id="name"
            value={formData.name}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label htmlFor="email">Your E-Mail</label>
          <input
            type="email"
            id="email"
            value={formData.email}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label htmlFor="gender">Gender</label>
          <select
            id="gender"
            value={formData.gender}
            onChange={handleInputChange}
          >
            <option value="">Select one</option>
            <option value="male">Male</option>
            <option value="female">Female</option>
          </select>
        </div>
        <div>
          <label htmlFor="country">Country</label>
          <select
            id="country"
            value={formData.country}
            onChange={handleInputChange}
          >
            <option value="">Select country</option>
            {countries.map((country) => (
              <option key={country.alpha2Code} value={country.name.common}>
                {country.flags.svg} {country.name.common}
              </option>
            ))}
          </select>
        </div>
        <div>
          <button type="submit">Submit</button>
        </div>
      </form>
    </div>
  );
};

export default ChatForm;
