import React, { useState } from 'react';
import { Country, City } from 'country-state-city';

const RestfulApi3 = () => {
  const [selectedCountry, setSelectedCountry] = useState('');
  const [selectedCity, setSelectedCity] = useState('');
  const [cities, setCities] = useState([]);

  const handleCountryChange = (event) => {
    const countryIsoCode = event.target.value;
    setSelectedCountry(countryIsoCode);
    setSelectedCity('');

    // Get cities of the selected country
    const countryCities = City.getCitiesOfCountry(countryIsoCode);
    setCities(countryCities);
  };

  const handleCityChange = (event) => {
    setSelectedCity(event.target.value);
  };

  const countries = Country.getAllCountries();

  return (
    <div>
      <div>
        <label htmlFor="country">Country</label>
        <select name="country" id="country" value={selectedCountry} onChange={handleCountryChange}>
          <option value=""></option>
          {countries.map((country) => (
            <option key={country.isoCode} value={country.isoCode}>
              {country.name}
            </option>
          ))}
        </select>
      </div>
      <div>
        <label htmlFor="city">City</label>
        <select name="city" id="city" value={selectedCity} onChange={handleCityChange}>
          <option value=""></option>
          {cities.map((city) => (
            <option key={city.name} value={city.name}>
              {city.name}
            </option>
          ))}
        </select>
      </div>
    </div>
  );
};

export default RestfulApi3;
