import { useEffect, useState } from "react";

const BasicForm = (props) => {
  const [firstName, setFirstName] = useState("")
  const [lastName, setLastName] = useState("")
  const [email, setEmail] = useState("")
  const [form, setForm] = useState(false)


  useEffect(() => {
    if(!firstName || !lastName || !email.includes('@')) {
      setForm(true)
    }else{
      setForm(false)
    }
  }, [firstName, lastName, email])
const handleFirstName = (e) => {
  setFirstName(e.target.value)
}

const handleLastName = (e) => {
  setLastName(e.target.value)
}

const handleEmail = (e) => {
  setEmail(e.target.value)
}

  const handleSubmit = (e) => {
    e.preventDefault()
    if(firstName.trim() === "" || lastName.trim() === "" || !email.includes('@')) {
      setForm(true)
      return
    }
    setForm(false)
    setFirstName("")
    setLastName("")
    setEmail("")
    console.log(firstName, lastName, email)
  }
  return (
    <form onSubmit={handleSubmit}>
      <div className='control-group'>
        <div className='form-control'>
          <label htmlFor='name'>First Name</label>
          {form && <p className="error-text">This is required</p>}
          <input onChange={handleFirstName} value={firstName} type='text' id='name' />
        </div>
        <div className='form-control'>
          <label htmlFor='name'>Last Name</label>
          {form && <p className="error-text">This is required</p>}
          <input onChange={handleLastName} value={lastName} type='text' id='name' />
        </div>
      </div>
      <div className='form-control'>
        <label htmlFor='name'>E-Mail Address</label>
        {form && <p >This is required</p>}
        <input onChange={handleEmail} value={email} type='email' id='name' />
      </div>
      <div className='form-actions'>
        <button disabled={form}>Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
